import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';
import NbaTable from './NbaTable'
import axios from 'axios';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';




class App extends Component {

  data = [{"team": "Indiana Pacers", "wins": "35", "loses": "17", "playoff": true}]

  constructor(props) {
    super(props);
    this.state = { currentMidSeason : this.state.midSeason.east, currentEndSeason : this.state.endSeason.east }
    // This binding is necessary to make `this` work in the callback
    this.setTeamsWest = this.setTeamsWest.bind(this);
    this.setTeamsEast = this.setTeamsEast.bind(this);
    this.getStandings = this.getStandings.bind(this);
    this.textInput = React.createRef(); 


  }
  componentDidMount() {
    axios.get('http://localhost:8000/2000')
      .then(res => {
        const midSeasonData = res.data.midSeason;
        const endSeasonData = res.data.endSeason;
        this.setState(state => ({
          midSeason: midSeasonData,
          endSeason: endSeasonData,          
        })
        )
      })
  }

  state = {
    midSeason: [],
    endSeason: [],
    currentMidSeason: [],
    currentEndSeason: []
  }

  setTeamsWest() {
    this.setState(state => ({
      currentMidSeason: this.state.midSeason.west,
      currentEndSeason: this.state.endSeason.west
    }));
  }

  setTeamsEast() {
    this.setState(state => ({
      currentMidSeason: this.state.midSeason.east,
      currentEndSeason: this.state.endSeason.east
    }));
  }

  getStandings(year) {
    axios.get('http://localhost:8000/' + year)
      .then(res => {
        const midSeasonData = res.data.midSeason;
        const endSeasonData = res.data.endSeason;
        this.setState(state => ({
          midSeason: midSeasonData,
          endSeason: endSeasonData,          
        })
        )
      })
  }

  render() {
    return (
      <div className="App">
        <div>
          <header className="App-header">
            <img src={logo} alt="NBA"/>
              <p>
                NBA to the max
              </p>
            <h5>
              Display NBA standings at the all star break and at the end of season
            </h5>
          </header>
        </div>
        <div
          style={{
            position: 'absolute', left: '20%', top: '160%',
            transform: 'translate(-50%, -50%)'
          }}
        >          
          <NbaTable
            tableData={this.state.currentMidSeason}
          />
        </div>
        <div style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '10vh'}}>
          <Form>
            <Form.Group controlId="formBasicEmail">
              <FormControl ref={this.textInput} type="text" onChange={() => this.getStandings()} />
              <Form.Control ref={this.textInput} type="numbers" placeholder="Enter year"/>
              <Form.Text className="text-muted">
              </Form.Text>
            </Form.Group>
            <Button variant="primary" type="submit" onClick={this.getStandings()}>>
              Submit
            </Button>
          </Form>
        </div>
        <div style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '20vh'}}>
          <button onClick={this.setTeamsWest}>
            West
          </button>
          <button onClick={this.setTeamsEast}>
            East
          </button>
        </div>
        <div
          style={{
            position: 'absolute', left: '80%', top: '160%',
            transform: 'translate(-50%, -50%)'
          }}
        >            
          <NbaTable
            tableData={this.state.currentEndSeason}
          />
        </div>
      </div>
    );
  }
}

export default App;
