import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';
import NbaTable from './NbaTable'
import axios from 'axios';


class App extends Component {

  data = [{"team": "Indiana Pacers", "wins": "35", "loses": "17", "playoff": true}]


  componentDidMount() {
    axios.get('http://localhost:8000/2000')
      .then(res => {
        const midSeason = res.data.midSeason;
        this.setState(state => ({
          midSeasonWestData: midSeason.west,
          midSeasonEastData: midSeason.east
        })
        )
      })
  }

  state = {
    midSeasonWestData: [],
    midSeasonEastData: []
  }



  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} alt="NBA"/>
          <p>
            NBA to the max
          </p>
          <h5>
          Display NBA standings at the all star break and at the end of season
          </h5>
        </header>
        <NbaTable
          tableData={this.state.midSeasonEastData}
        />
      </div>
    );
  }
}

export default App;
