import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';
import NbaTable from './NbaTable'
import axios from 'axios';


class App extends Component {

  data = [{"team": "Indiana Pacers", "wins": "35", "loses": "17", "playoff": true}]

  constructor(props) {
    super(props);
    // This binding is necessary to make `this` work in the callback
    this.setTeamsWest = this.setTeamsWest.bind(this);
  }
  componentDidMount() {
    axios.get('http://localhost:8000/2000')
      .then(res => {
        const midSeasonData = res.data.midSeason;
        const endSeasonData = res.data.endSeason;
        this.setState(state => ({
          midSeason: midSeasonData,
          endSeason: endSeasonData,          
        })
        )
      })
  }

  state = {
    midSeason: [],
    endSeason: []
  }

  setTeamsWest() {
    this.setState(state => ({
      midSeasonData: midSeasonWestData,
      endSeasonData: endSeasonWestData
    }));
  }



  render() {
    return (
      <div className="App">
        <div>
          <header className="App-header">
            <img src={logo} alt="NBA"/>
              <p>
                NBA to the max
              </p>
            <h5>
              Display NBA standings at the all star break and at the end of season
            </h5>
          </header>
        </div>
        <div
          style={{
            position: 'absolute', left: '20%', top: '160%',
            transform: 'translate(-50%, -50%)'
          }}
        >          
          <NbaTable
            tableData={this.state.midSeasonData}
          />
        </div>
        <div style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '10vh'}}>
          <button onClick={this.setTeamsWest}>
            West
          </button>
          <button>
            East
          </button>
        </div>
        <div
          style={{
            position: 'absolute', left: '80%', top: '160%',
            transform: 'translate(-50%, -50%)'
          }}
        >            
          <NbaTable
            tableData={this.state.endSeasonData}
          />
        </div>
      </div>
    );
  }
}

export default App;
